import React, { Component } from "react";
import { connect } from "react-redux";
import { getTeamData } from "../../actions/Teams/TeamsAPI";

const mapStateToProps = state => {
  return {
    team: state.teamReducer.team,
    data: state.teamReducer.data
  };
};

const mapDispatchToProps = dispatch => {
  return {
    // setTeam: team => dispatch(setTeam(team)),
    getTeamData: conference => dispatch(getTeamData(conference))
  };
};

const renderLogo = props => {
  if (this.props.team.length > 0) {
    return <img class="h-40" src={this.props.team.logos[0]} alt="" />;
  }
};

class Team extends Component {
  constructor(props) {
    super(props);

    console.log(props);
  }

  componentDidMount() {
    getTeamData(this.props.match.params.teamName);
  }

  render() {
    if (this.props.team) {
      const styles = {
        // background: this.props.team.alt_color
      };
    }
    return (
      <div className="">
        <div className="flex-row">
          <h2 className="text-6xl mt-8">{this.props.match.params.teamName}</h2>
          <renderLogo />
        </div>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Team);
