import React, { Component } from 'react';
import { connect } from "react-redux";
import { getRankings } from '../../actions/actionsAPI';

const mapDispatchToProps = dispatch => {
    return {
        getRankings: team => dispatch(getRankings(team))
    }
}

const mapStateToProps = state => {
    return {
        rankings: state.rankingsReducer.rankings
    }
}

class Rankings extends Component {
    constructor(props) {
        super(props)

        this.state = {
            team: '',
            year: ''
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        // console.log(event.target.value)
        this.setState({team: event.target.value});
        console.log(this.state.team)
      }

    handleSubmit(event) {
        event.preventDefault()
        this.props.getRankings(this.state.team)
    }

    render() {
        return(
            <div>
                <div>Rankings</div>
                <form className="text-black" onSubmit={this.handleSubmit}>
                    <label>
                    Team:
                    <input type="text" value={this.state.team} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Rankings)
