import { GET_RANKINGS } from '../constants/action-types'


export default function rankingsReducer(state = [], action) {
    if (action.type === GET_RANKINGS) {
        return Object.assign({}, state, {
            ...state,
            rankings: {
                AP: action.payload.data[0].polls[1].ranks,
                Playoff: action.payload.data[0].polls[3].ranks
            }
        })
    }
    return state
}
