import { SET_TEAM } from '../constants/action-types'

export default function teamReducer(state = [], action) {
    if (action.type === SET_TEAM) {
        console.log(action)
        return Object.assign({}, state, {
            ...state,
            team: action.payload
        })
    }
    return state
}
